type.description:

Copies files into a destination directory. This task can also rename and filter files as it copies. The task 
implements <a href='type://org.gradle.api.file.CopySpec CopySpec'>org.gradle.api.file.CopySpec CopySpec</a> for specifying what to copy. 

<p> Examples: 
<pre autoTested=''> 
task copyDocs(type: Copy) { 
    from 'src/main/doc' 
    into 'build/target/doc' 
} 

//for Ant filter 
import org.apache.tools.ant.filters.ReplaceTokens 

//for including in the copy task 
def dataContent = copySpec { 
    from 'src/data' 
    include '*.data' 
} 

task initConfig(type: Copy) { 
        include '**&#47;*.properties' 
        include '**&#47;*.xml' 
        filter(ReplaceTokens, tokens: [version: '2.3.1']) 
    } 
    from('src/main/config') { 
        exclude '**&#47;*.properties', '**&#47;*.xml' 
    } 
    from('src/main/languages') { 
        rename 'EN_US_(.*)', '$1' 
    } 
    into 'build/target/config' 
    exclude '**&#47;*.bak' 

    includeEmptyDirs = false 

    with dataContent 
} 
</pre>
